mapper.insert:
		INSERT INTO emp_tbl ( last_name
		       , gender
		       , email
	         ) VALUES ( #{lastName}
	           , #{gender}
	           , #{email}
            )

mapper.update:
UPDATE emp_tbl
   SET last_name = #{lastName}
       , gender = #{gender}
       , email = #{email}
 WHERE id = #{id}

mapper.select:
SELECT id AS ID
       , last_name AS LASTNAME
       , email AS EMAIL
       , gender AS GENDER
  FROM ${TABLE_NAME} 
 WHERE id = ${ID}
   and last_name = #{LAST_NAME}
